             !this._getElementById(aID).checked;
if (!disabled) {
  this.removeAttribute(aID + "-disabled");
  opacity = this._getElementById(aID + "Opacity").value;
}
else {
  if (disableBg)
    this._getElementById("bg").disabled = true;
  this.setAttribute(aID + "-disabled", true);
