implementation.constructor:

   eval("this.open ="+this.open.toString().replace(
     'if (uri)',
     'if (!("TMP_openURL" in win)) win.TMP_openURL = Tabmix.getTopWin().TMP_openURL; if (uri)'
   ).replace(
      'win.open(uri.spec);',
      'win.TMP_openURL(uri.spec, aEvent);'
   ).replace(
      'win.open(href);',
      'win.TMP_openURL(href, aEvent);'
   ));
