implementation.constructor:

   eval("this.open ="+this.open.toString().replace(
     'if (uri)',
     'if (!("openURL" in win.Tabmix)) win.Tabmix.openURL = Tabmix.getTopWin().Tabmix.openURL; if (uri)'
   ).replace(
      'win.open(uri.spec);',
      'win.Tabmix.openURL(uri.spec, aEvent);'
   ).replace(
      'win.open(href);',
      'win.Tabmix.openURL(href, aEvent);'
   ));
